jQuery(document).ready(function() {
	$(".login-form").submit
	(
		function()
		{
			if ($.trim(document.loginform.sLoginName.value) == "" || $.trim(document.loginform.sPassword.value) == "") {
				$('.alert-danger', $('.login-form')).show().find("span").text("请输入用户名和密码");
				return false;
			}
			
			return true;
		}
	)
});